class Environment():    def __init__(self, **kwargs):        self.kwargs = kwargs        self.validate_kwargs()                self.population = []                        self.population_size = getattr(kwargs, 'population_size', 20)        self.crossover_method = getattr(kwargs, 'crossover_method', 'regular')        self.mutation_rate = getattr(kwargs, 'mutation_rate', 0.1)        self.mutation_amplitude = getattr(kwargs, 'mutation_amplitude', 1)        self.diversity_criteria = getattr(kwargs, 'diversity_criteria', 'none')                    def validate_kwargs(self):        # print(self.kwargs)        pass                    def initialize_population(self, individual_class, individual_kwargs):        for _ in range(self.population_size):            self.population.append(individual_class(**individual_kwargs))        