from ..core.individual import Individualimport randomclass Sphere(Individual):    def __init__(self, **kwargs):        super().__init__()        self.size = getattr(kwargs, 'size', 10)        self.lower_bound = getattr(kwargs, 'lower_bound', -5)        self.upper_bound = getattr(kwargs, 'upper_bound', 5)        self.chromosome = [random.uniform(self.lower_bound, self.upper_bound) for _ in range(self.size)]                    def get_fitness_value(self):        if self.fitness_value is not None:            return self.fitness_value        return 42        